data(mtcars)
compute_stats &lt;- function(x) { stats &lt;- list( mean = mean(x, na.rm = TRUE), median = median(x, na.rm = TRUE), variance = var(x, na.rm = TRUE), IQR = IQR(x, na.rm = TRUE), min = min(x, na.rm = TRUE), max = max(x, na.rm = TRUE) ) return(stats) }
for (col in colnames(mtcars)) { if (is.numeric(mtcars[[col]])) { cat(“”, col, “İstatistikleri:”) print(compute_stats(mtcars[[col]])) } }
sapply_results &lt;- sapply(mtcars, function(x) { if (is.numeric(x)) compute_stats(x) else NULL }) print(sapply_results)
apply_results &lt;- apply(mtcars, 2, function(x) { if (is.numeric(x)) compute_stats(x) else NULL }) print(apply_results)
